function pad(n){return String(n).padStart(2,'0');}
const now=new Date();
const order_id=`#HOXOH-${now.getFullYear()}-${pad(now.getMonth()+1)}${pad(now.getDate())}-${Math.floor(Math.random()*90+10)}`;
const body=(bundle.input&&bundle.input.body)?bundle.input.body:bundle;
const get=(o,p,d)=>{try{return p.split('.').reduce((x,k)=>(x&&x[k]!=null)?x[k]:null,o)??d;}catch(e){return d;}};
const out={
 order_id,
 customer_name:get(body,'customer.name','')||get(body,'customer_name','')||get(body,'buyer.name',''),
 customer_email:get(body,'customer.email','')||get(body,'customer_email','')||get(body,'buyer.email',''),
 product_title:get(body,'product.name','')||get(body,'product_title','')||get(body,'product',''),
 price:get(body,'price','')||get(body,'payment.amount',''),
 currency:get(body,'currency','USD')||get(body,'payment.currency','USD'),
 payment_method:get(body,'payment.method','PayPal')||get(body,'payment_method','PayPal'),
 transaction_id:get(body,'payment.transaction_id','')||get(body,'transaction_id','')||get(body,'payment.id',''),
 note:get(body,'note','')||get(body,'message','')||get(body,'project_brief',''),
 created_at:new Date().toISOString().replace('T',' ').replace('Z',' UTC')
};
output=out;
